var express = require('express');
var db = require('./db');
var path = require('path');
var favicon = require('serve-favicon');
var logger = require('morgan');
var cookieParser = require('cookie-parser');
var bodyParser = require('body-parser');
var partials = require('express-partials');

var routes = require('./routes/index');
var users = require('./routes/users');

var app = express();

// view engine setup
app.set('views', path.join(__dirname, 'views'));
//app.set('view engine', 'ejs');
//app.set(partials());  //

// uncomment after placing your favicon in /public
//app.use(favicon(path.join(__dirname, 'public', 'favicon.ico')));
app.use(logger('dev'));
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false }));
app.use(cookieParser());
app.use(express.static(path.join(__dirname, 'public')));  //
app.use(express.static(path.join(__dirname, 'node_modules')));  //
app.use('/', routes);

// catch 404 and forward to error handler
app.use(function(req, res, next) {
  var err = new Error('Not Found');
  err.status = 404;
  next(err);
});

// error handlers

// development error handler
// will print stacktrace
if (app.get('env') === 'development') {
  app.use(function(err, req, res, next) {
    res.status(err.status || 500);
    res.render('error', {
      message: err.message,
      error: err
    });
  });
}

// production error handler
// no stacktraces leaked to user
app.use(function(err, req, res, next) {
  res.status(err.status || 500);
  res.render('error', {
    message: err.message,
    error: {}
  });
});


module.exports = app;
```
### REST API 
API:
* todo `/api/todos``GET`
* todo `/api/todo``POST`
* todo`/api/todo/:id``PUT`
* todo`/api/todo/:id``DELETE`


API
```javascript
/* restful api */
//get all todo
router.get('/api/todos', function(req, res, next){
    Todo
        .find()
        .sort('-update_at')
        .exec(function(err, todos){
            if(err){
                res.send(err);
            }
            res.json(todos);
        });
});

//POST that create todo and return all todos
router.post('/api/todo', function(req, res, next){
    new Todo({
        content : req.body.content,
        update_at : new Date()
    }).save(function(err, todo, count){
        if(err){
            res.send(err);
        }
        Todo
            .find()
            .sort('-update_at') //
            .exec(function(err, todos){
                if(err){
                    res.send(err);
                }
                res.json(todos);
            });
    })
});

//
router.delete('/api/todo/:id', function(req, res, next){
    Todo.findById(req.params.id, function(err, todo){
        todo.remove(function(err, todo){
            if(err){
                res.send(err);
            }
            Todo
                .find()
                .sort('-update_at') //
                .exec(function(err, todos){
                    if(err){
                        res.send(err);
                    }
                    res.json(todos);
                });
        })
    })
});
//Update
router.put('/api/todo/:id', function(req, res, next){
    //
    Todo.findById(req.params.id, function(err, todo){
            if(err){
                res.send(err);
            }
        todo.content = req.body.content;
        todo.save(function(err){
            console.log('update success');
        });
    });
            Todo
                .find()
                .sort('-update_at') //
                .exec(function(err, todos){
                    if(err){
                        res.send(err);
                    }
                    res.json(todos);
                });
});
```
