```javascript
function findAll(arry, x){
	var results = [],
	length = arry.length,
	pos=0;
	while(pos<length){
		pos = arry.indexOf(x, pos);
		if(pos == -1){
			break;
		}
		results.push(pos);
		pos++;
	}
	return results;
}
var arry = [1,2,3,1,1,4,5,1];
console.log(findAll(arry,1));	//[ 0, 3, 4, 7 ]
